"use strict";

var wrench = require('wrench');
var us = require('underscore');
var env = process.env.NODE_ENV || 'dev';

exports.loadConfig = function(groupName){
  var loadConfigFiles = function(){
    if(env === 'dev'){
      var dev = require('../../settings/dev.js');
      GLOBAL.ee_config = {};
      us.extend(GLOBAL.ee_config,dev);
    }
  };

  if(!GLOBAL.ee_config){
    loadConfigFiles();
  }
  //console.log(JSON.stringify(GLOBAL.ee_config, null, 2));
  return GLOBAL.ee_config[groupName];
};

